import React, { useState } from "react";
import Form from 'react-bootstrap/Form';

function Formcuenta() {
  const [formData, setFormData] = useState({
    email: '',
    telefono: '',
    contraseña: '',
    nombres: '',
    apellidos: '',
    cedula: '',
    usuario: '',
    confirmPassword: '',
    aceptoTerminos: false,
    aceptoContacto: false,
    codigoTelefono:''
  })

  const [emailError, setEmailError] = useState('');
  const [cedulaError, setCedulaError] = useState ('');
  const [usuarioError, setUsuarioError] = useState('');
  const [telefonoError, setTelefonoError] = useState('');
  const [emailValidado, setEmailValidado] = useState(false);
  const [nombreValidado, setNombreValidado] = useState(false);
  const [telefonoValidado, setTelefonoValidado] = useState(false);
  const [passwordsMatch, setPasswordsMatch] = useState(true);
  const [buttonEnabled, setButtonEnabled] = useState({
    nombre: false,
    telefono: false,
    contraseña: false
  });


  const isFormDataComplete = () => {
    // Verificar si todos los campos del formData están llenos
    for (const key in formData) {
      if (formData[key] === '') {
        return false;
      }
    }
    return true;
  };

   // Función para validar el email
   const validateEmail = (email) => {
    const emailRegex = /\S+@\S+\.\S+/;
    return emailRegex.test(email);
  };
  

  const handleSubmit = (event) => {
    event.preventDefault();

      // Validar que el email sea válido
    if (!validateEmail(formData.email)) {
      setEmailError('El correo electrónico ingresado no es válido');
      return;
    }
    // Validar que se hayan aceptado los términos
    if (!formData.aceptoTerminos) {
      setEmailError('Debes aceptar los términos y condiciones');
      return;
    }
    
    //Verificar existencia de correo
    fetch('http://localhost:5000/verificar_correo', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ email: formData.email }),
    })
    .then(response => response.json())
    .then(data => {
      if (data.error) {
        setEmailError("Este correo ya está en uso");
      } else {
        setShowEmailForm(false);
        setEmailValidado(true);
        setButtonEnabled(prevState => ({ ...prevState, nombre: true }));
        console.log(formData.email);
        console.log(formData.aceptoTerminos);
      }
    })
    .catch(error => {
      console.error('Error al verificar correo:', error);
    });


      // Validar el formulario de nombres
    if (formData.nombres === "" || formData.apellidos === "" || formData.cedula === "" || formData.usuario === "") {
      // Mostrar mensaje de error o tomar otra acción según sea necesario
      setUsuarioError("Todos los campos son requeridos");
      return;
    }

    // Validar que el número de cédula tenga 10 dígitos numéricos
    if (!/^\d{10}$/.test(formData.cedula)) {
      // Mostrar mensaje de error o tomar otra acción según sea necesario
      setCedulaError("El número de cédula debe tener 10 dígitos");
      return;
    }

    //Verificar la existencia del numero de cedula y usuario
    fetch('http://localhost:5000/verificar_datos', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ 
        cedula: formData.cedula,
        usuario: formData.usuario
      }),
    })
    .then((response) => response.json())
    .then(data => {
      if (data.cedula_error) {
        setCedulaError(data.cedula_error);
      } else if (data.usuario_error) {
        setUsuarioError(data.usuario_error);
      } else {
        // Si no hay errores, puedes proceder al siguiente paso
        setShowNombresForm(false);
        setNombreValidado(true);
        setButtonEnabled(prevState => ({ ...prevState, telefono: true }));
        console.log(formData.nombres);
        console.log(formData.apellidos);
        console.log(formData.cedula);
        console.log(formData.usuario);
      }
    })
    .catch((error) => {
      console.log("Ocurrió un error al verificar la cedula y el usuario", error);
    });

     // Validar que el número de teléfono tenga exactamente 9 dígitos numéricos
    if (!/^\d{9}$/.test(formData.telefono)) {
      // Mostrar mensaje de error o tomar otra acción según sea necesario
      setTelefonoError("El número de teléfono debe tener 9 dígitos numéricos");
      return;
    }

    if (!formData.aceptoContacto) {
      // Mostrar mensaje de error o tomar otra acción según sea necesario
      setTelefonoError("Debes aceptar que te contacten por WhatsApp y/o SMS");
      return;
    }
    
     // Verificar existencia de numero de telefono 
    fetch('http://localhost:5000/verificar_telefono', {
      method: 'POST',
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ telefono: formData.telefono }),
    })
    .then(response => response.json())
    .then(data => {
      if (data.error) {
        setTelefonoError("Este numero de telefono ya esta en uso");
      } else {
        // Si el número de teléfono no está en uso, enviar el código de verificación desde el servidor
        fetch('http://localhost:5000/enviar_codigo_verificacion', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ telefono: formData.telefono }),
        })
        .then(response => response.json())
        .then(data => {
          if (data.success) {
            // Si el código se envió correctamente, mostrar el formulario de validación
            handleShowValidacionForm();
            setTelefonoError(""); // Limpiar el error de teléfono si lo hay
          } else {
            setTelefonoError("Error al enviar el código de verificación. Inténtalo de nuevo más tarde.");
          }
        })
        .catch(error => {
          console.error('Error al enviar el código de verificación:', error);
        });
      }
    })
    .catch(error => {
      console.error('Error al verificar numero de telefono', error);
    });

    // Validar que las contraseñas coincidan
    if (formData.contraseña.length < 8 || !/[a-zA-Z]/.test(formData.contraseña) || !/\d/.test(formData.contraseña)) {
      console.log('La contraseña debe tener al menos 8 caracteres, incluyendo al menos una letra y un número');
      return;
    }


    // Validar que las contraseñas coincidan
    if (formData.contraseña !== formData.confirmPassword) {
      console.log('Las contraseñas no coinciden');
      return;
    }

    console.log(formData.contraseña);
    console.log(formData.confirmPassword);

    if (isFormDataComplete()) {
    // Enviar datos al servidor
    fetch('http://localhost:5000/insertar', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData),
    })
    .then(response => response.json())
    .then(data => {
      console.log('Respuesta del servidor:', data);
    })
    .catch(error => {
      console.error('Error al enviar datos:', error);
      });
    } else {
      console.log('Formulario incompleto. No se envían datos.');
    }
  };

  // Función para manejar la validación del código de verificación
const handleSubmitCodigoVerificacion = (event) => {
  event.preventDefault();

  const codigoVerificacion = event.target['verification-code'].value;  // Obtener el código de verificación del formulario

  fetch('http://localhost:5000/verificar_codigo_verificacion', {
      method: 'POST',
      headers: {
          'Content-Type': 'application/json'
      },
      body: JSON.stringify({ 
        codigo_verificacion: codigoVerificacion,
        telefono: formData.telefono
      }),  // Enviar el código de verificación al servidor
  })
  .then(response => response.json())
  .then(data => {
      if (data.success) {
          // Si la verificación es exitosa, actualizar el estado de la aplicación
          setShowValidacionForm(false);
          setTelefonoValidado(true);
          setButtonEnabled(prevState => ({ ...prevState, contraseña: true }));
          console.log(formData.telefono);
          console.log(formData.aceptoContacto);
      } else {
          // Si la verificación falla, mostrar un mensaje de error
          console.error('Error al verificar código de verificación:', data.error);
          // Aquí puedes mostrar un mensaje de error al usuario o tomar otra acción según necesites
      }
  })
  .catch(error => {
      console.error('Error al verificar código de verificación:', error);
      // Aquí puedes manejar errores de red u otros errores de comunicación con el servidor
  });
};
  

  const handleInputChange = (fieldName, value) => {
    if (fieldName === "contraseña") {
      // Si el campo que se está cambiando es la contraseña, también actualiza confirmPassword
      setFormData(prevFormData => ({
        ...prevFormData,
        contraseña: value,
        // Actualizar confirmPassword si las contraseñas coinciden
        confirmPassword: value === prevFormData.confirmPassword ? value : prevFormData.confirmPassword
      }));
      // Verificar si las contraseñas coinciden y actualizar el estado
      setPasswordsMatch(value === formData.confirmPassword);
    } else if (fieldName === "confirmPassword") {
      // Si el campo que se está cambiando es la confirmación de la contraseña, actualiza solo ese campo
      setFormData(prevFormData => ({
        ...prevFormData,
        confirmPassword: value
      }));
      // Verificar si las contraseñas coinciden y actualizar el estado
      setPasswordsMatch(formData.contraseña === value);
    } else {
      // Para otros campos, simplemente actualiza el estado del formulario
      setFormData(prevFormData => ({
        ...prevFormData,
        [fieldName]: value
      }));
    }
  };

  const handleCheckChange = () => {
    setFormData(prevFormData => ({
      ...prevFormData,
      aceptoTerminos: !prevFormData.aceptoTerminos
    }));
  };

  const handleCheckboxChange = () => {
    setFormData(prevFormData =>({
      ...prevFormData,
      aceptoContacto: !prevFormData.aceptoContacto
    }));
  };

  const [showEmailForm, setShowEmailForm] = useState(false);
  const [showNombresForm, setShowNombresForm] = useState(false);
  const [showTelefonoForm, setShowTelefonoForm] = useState(false);
  const [showContraseñaForm, setShowContraseñaForm] = useState(false);
  const [showValidacionForm, setShowValidacionForm] = useState(false);

  const handleShowValidacionForm = () =>{
    setShowValidacionForm(true);
    setShowContraseñaForm(false);
    setShowNombresForm(false);
    setShowTelefonoForm(false);
    setShowEmailForm(false);
  }

  const handleShowContraseñaForm = () => {
    setShowContraseñaForm(true);
    setShowValidacionForm(false);
    setShowTelefonoForm(false);
    setShowEmailForm(false);
    setShowNombresForm(false);
  }

  const handleShowTelefonoForm = () => {
    setShowTelefonoForm(true);
    setShowValidacionForm(false);
    setShowEmailForm(false);
    setShowNombresForm(false);
    setShowContraseñaForm(false);
  };

  const handleShowNombresForm = () => {
    setShowNombresForm(true);
    setShowValidacionForm(false);
    setShowEmailForm(false)
    setShowTelefonoForm(false);
    setShowContraseñaForm(false);
  };
  
  const handleShowEmailForm = () => {
    setShowEmailForm(true);
    setShowValidacionForm(false);
    setShowNombresForm(false);
    setShowTelefonoForm(false);
    setShowContraseñaForm(false);
  };

  return (
    <div className="contenedor-formcuenta">
      <div className="row justify-content-center">
        <div className="col-4">
          <div className="h4 text-center mb-4">Completa los datos para crear tu cuenta</div>
          
          {showEmailForm ? (
            // Mostrar el formulario para agregar el email
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="email" className="h5">Ingresa tu Email</label>
                <h5 className="h6">Asegúrate de tener acceso a él.</h5>
                <input 
                type="email" 
                className="form-control" 
                id="email" 
                value={formData.email} 
                onChange={(e) => handleInputChange('email', e.target.value )}
                />
                {emailError && <div className="text-danger">{emailError}</div>}
              </div>
              <Form.Group className="mb-3 form-check">
                <Form.Check 
                type="checkbox" 
                label="Acepto los Términos y condiciones y autorizo el uso de mis datos de acuerdo a la Declaración de Privacidad." 
                onChange={handleCheckChange}
                checked={formData.aceptoTerminos}
                />
              </Form.Group>
              <button type="submit" className="btn btn-warning botonn">Guardar</button>
            </form>
          ) : showTelefonoForm ? (
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="telefono" className="h5">Ingresa tu numero de teléfono</label>
                <h5 className="h6">Te enviaremos un código por SMS para validarlo. Con este teléfono podrás iniciar sesión en tu cuenta.</h5>
                <br></br>
                <div className="input-group">
                  <span className="input-group-text" id="basic-addon1">+593</span>
                  <input 
                  type="text" 
                  className="form-control" 
                  placeholder="Numero de telefono" 
                  value={formData.telefono} 
                  onChange={(e) => handleInputChange('telefono', e.target.value )} 
                  aria-label="Telefono" aria-describedby="basic-addon1" 
                  />
                  {telefonoError && <div className="text-danger">{telefonoError}</div> }
                </div>
              </div>
              <Form.Group className="mb-3 form-check">
                <Form.Check 
                type="checkbox" 
                label="Acepto que me contacten por WhatsApp y/o SMS a este número" 
                onChange={handleCheckboxChange}
                checked={formData.aceptoContacto}
                />
                </Form.Group>
              <button type="submit" className="btn btn-warning botonn">Guardar</button>
            </form>
          ) : showContraseñaForm ? (
            <Form onSubmit={handleSubmit}>
              <div className="form-group">
              <label htmlFor="password" className="h5">Crear contraseña</label>
                <input 
                type="password" 
                className="form-control" 
                id="contraseña" 
                placeholder="Contraseña" 
                value={formData.contraseña} 
                onChange={(e) => handleInputChange('contraseña', e.target.value )} 
                />
              </div>
              <div className="form-check">
                <label className="form-check-label" htmlFor="invalidCheck">
                Mínimo 8 caracteres con letras y números.                
                </label>
              </div>
              <div className="form-group">
                <label htmlFor="confirmPassword" className="h5">Confirmar contraseña</label>
                <input 
                type="password" 
                className="form-control" 
                id="confirmPassword" 
                placeholder="Confirmar Contraseña"
                value={formData.confirmPassword} 
                onChange={(e) => handleInputChange('confirmPassword', e.target.value )} 
                />
                {formData.contraseña !== formData.confirmPassword && formData.confirmPassword !== '' && (
                  <div className="text-danger">Las contraseñas no coinciden.</div>
                )}
                {formData.contraseña === formData.confirmPassword && formData.confirmPassword !== '' && (
                  <div className={passwordsMatch ? "text-success" : "text-danger"}>
                    {passwordsMatch ? "Las contraseñas coinciden." : "Las contraseñas no coinciden."}
                  </div>
                )}
              </div>
              <button type="submit" className="btn btn-warning botonn">Crear cuenta</button>
            </Form>
          ): showNombresForm ? (
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="nombres" className="h5">Elige cómo quieres que te llamemos</label>
                <h5 className="h6">Verán el nombre que elijas todas las personas que interactúen contigo y tu videoteca</h5>
              </div>
              <div className="row mb-3">
                <div className="col">
                  <label htmlFor="nombre" className="form-label">Nombre</label>
                  <input 
                  type="text" 
                  className="form-control" 
                  id="nombre" 
                  value={formData.nombres} 
                  onChange={(e) => handleInputChange('nombres', e.target.value )} 
                />
                  {formData.nombres === '' && (
                    <div className="text-danger">Nombre es requerido</div>
                  )}
                </div>
                <div className="col">
                  <label htmlFor="apellido" className="form-label">Apellido</label>
                  <input 
                  type="text" 
                  className="form-control" 
                  id="apellido" 
                  value={formData.apellidos} 
                  onChange={(e) => handleInputChange('apellidos', e.target.value )} 
                />
                  {formData.apellidos === '' && (
                    <div className="text-danger">Apellido es requerido</div>
                  )}
                </div>
              </div>
              <div className="row mb-3">
                <div className="col">
                  <label htmlFor="cedula" className="form-label">Número de Cédula</label>
                  <input 
                  type="text" 
                  className="form-control" 
                  id="cedula" 
                  value={formData.cedula} 
                  onChange={(e) => handleInputChange('cedula', e.target.value )} 
                />
                {cedulaError && <div className="text-danger">{cedulaError}</div> }
                </div>
                <div className="col">
                  <label htmlFor="validationCustomUsername" className="form-label">Nombre de usuario</label>
                  <div className="input-group has-validation">
                    <span className="input-group-text" id="inputGroupPrepend">@</span>
                    <input 
                    type="text" 
                    className="form-control" 
                    id="validationCustomUsername" 
                    value={formData.usuario} 
                    onChange={(e) => handleInputChange('usuario', e.target.value )} 
                    aria-describedby="inputGroupPrepend" 
                  />
                  {usuarioError && <div className="text-danger">{usuarioError}</div> }
                  </div>
                </div>
              </div>
              <button type="submit" className="btn btn-warning botonn">Guardar</button>
            </form>
          ): showValidacionForm ? (
            <form onSubmit={handleSubmitCodigoVerificacion}>
              <div className="form-group">
                <label>Te hemos enviado un codigo de verificacion al numero celular registrado</label><br/>
                <label htmlFor="verification-code">Ingrese el código de verificación:</label><br/><br/>
                <input 
                type="text" 
                className="form-control" 
                id="verification-code" 
                name="verification-code" 
                required 
                /><br/>
                <button type="submit" className="btn btn-warning botonn">Verificar Código</button>
              </div>
            </form>
          ): showValidacionEmailForm ? (
            <form>
              <div className="form-group">
                <label>Te hemos enviado un codigo de verificacion al correo registrado</label><br/>
                <label htmlFor="verification-code">Ingrese el código de verificación:</label><br/><br/>
                <input 
                type="text" 
                className="form-control" 
                id="verification-code" 
                name="verification-code" 
                required 
                /><br/>
                <button type="submit" className="btn btn-warning botonn">Verificar Código</button>
              </div>
            </form>
          ): (
            // Mostrar los botones normales
            <ul className="list-unstyled">
              <li className="d-flex justify-content-between align-items-center mb-3">
                <label>Agrega tu e-mail</label>
                {emailValidado ? (
                <button className="btn btn-success botonn" disabled>
                  <i className="fa fa-check-circle"></i>
                </button>
                ) : (
                <button className="btn btn-warning botonn" onClick={handleShowEmailForm}>Agregar</button>
                )}
              </li>
              <li className="d-flex justify-content-between align-items-center mb-3">
                <label>Elige tu nombre</label>
                {nombreValidado ? (
                <button className="btn btn-success botonn" disabled>
                  <i className="fa fa-check-circle"></i>
                </button>
                ): (
                <button className="btn btn-warning botonn" onClick={handleShowNombresForm} disabled={!buttonEnabled.nombre}>Agregar</button>
                )}
              </li>
              <li className="d-flex justify-content-between align-items-center mb-3">
                <label>Validar tu telefóno</label>
                {telefonoValidado ?(
                <button className="btn btn-success botonn" disabled>
                  <i className="fa fa-check-circle"></i>
                </button>
                ):( 
                <button className="btn btn-warning botonn" onClick={handleShowTelefonoForm} disabled={!buttonEnabled.telefono}>Validar</button>
                )}
              </li>
              <li className="d-flex justify-content-between align-items-center">
                <label>Crear contraseña</label>
                <button className="btn btn-warning botonn" onClick={handleShowContraseñaForm} disabled={!buttonEnabled.contraseña}>Crear</button>
              </li>
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Formcuenta;
