import React, { useState } from "react";
import { Link } from 'react-router-dom';
import Form from 'react-bootstrap/Form';

function Formcuenta() {
  const [formData, setFormData] = useState({
    email: '',
    telefono: '',
    contraseña: '',
    nombres: '',
    apellidos: '',
    cedula: '',
    usuario: '',
    confirmPassword: ''
  });

  const [currentForm, setCurrentForm] = useState(null); // Estado para rastrear el formulario actual

  // Función para manejar cambios en los campos del formulario
  const handleInputChange = (field, value) => {
    setFormData({ ...formData, [field]: value });
  };

  // Función para enviar los datos al servidor
  const handleSubmit = (event) => {
    event.preventDefault();
    // Enviar los datos al servidor
    fetch('http://localhost:5000/insertar', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData),
    })
      .then(response => response.json())
      .then(data => {
        console.log('Respuesta del servidor:', data);
      })
      .catch(error => {
        console.error('Error al enviar datos:', error);
      });
  };

  // Función para verificar si todos los campos están completos
  const isFormCompleted = () => {
    return Object.values(formData).every(value => value !== '');
  };

  return (
    <div className="contenedor-formcuenta">
      <div className="row justify-content-center">
        <div className="col-4">
          <div className="h4 text-center mb-4">Completa los datos para crear tu cuenta</div>
          <form onSubmit={handleSubmit}>
            {/* Condicionales para mostrar los formularios */}
            {currentForm === 'email' && (
              <div>
                <div className="form-group">
                  <label htmlFor="email" className="h5">Ingresa tu Email</label>
                  <h5 className="h6">Asegúrate de tener acceso a él.</h5>
                  <input 
                    type="email" 
                    className="form-control" 
                    id="email" 
                    value={formData.email} 
                    onChange={(e) => handleInputChange('email', e.target.value)}
                  />
                </div>
                <Form.Group className="mb-3 form-check">
                  <Form.Check type="checkbox" label="Acepto los Términos y condiciones y autorizo el uso de mis datos de acuerdo a la Declaración de Privacidad." />
                </Form.Group>
              </div>
            )}
            {currentForm === 'telefono' && (
              <div>
                <div className="form-group">
                  <label htmlFor="telefono" className="h5">Ingresa tu numero de teléfono</label>
                  <h5 className="h6">Te enviaremos un código por SMS para validarlo. Con este teléfono podrás iniciar sesión en tu cuenta.</h5>
                  <br></br>
                  <div class="input-group">
                    <span class="input-group-text" id="basic-addon1">+593</span>
                    <input 
                      type="text" 
                      class="form-control" 
                      placeholder="Numero de telefono" 
                      value={formData.telefono} 
                      onChange={(e) => handleInputChange('telefono', e.target.value)}
                      aria-label="Telefono" aria-describedby="basic-addon1" />
                  </div>
                </div>
                <div class="form-check">
                  <input class="form-check-input" type="checkbox" value="" id="invalidCheck" />
                  <label class="form-check-label" for="invalidCheck">
                  Acepto que me contacten por WhatsApp y/o SMS a este número
                  </label>
                  <div class="invalid-feedback">
                    You must agree before submitting.
                  </div>
                </div>
              </div>
            )}
            {/* Agrega los demás formularios aquí de manera similar */}
            {/* Botón para enviar los datos al servidor */}
            <button type="submit" className="btn btn-warning botonn" disabled={!isFormCompleted()}>Guardar</button>
          </form>
          {/* Sección de botones para cambiar de formulario */}
          <ul className="list-unstyled">
            <li className="d-flex justify-content-between align-items-center mb-3">
              <label>Agrega tu e-mail</label>
              <button className="btn btn-warning botonn" onClick={() => setCurrentForm('email')}>Agregar</button>
            </li>
            <li className="d-flex justify-content-between align-items-center mb-3">
              <label>Elige tu nombre</label>
              <button className="btn btn-warning botonn" onClick={() => setCurrentForm('nombres')}>Agregar</button>
            </li>
            <li className="d-flex justify-content-between align-items-center mb-3">
                <label>Validar tu telefóno</label>
                <button to="" className="btn btn-warning botonn" onClick={()=> setCurrentForm()}>Validar</button>
            </li>
            {/* Agrega los demás botones aquí de manera similar */}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Formcuenta;
