import React, { useState } from "react";
import { Link } from 'react-router-dom';
import Form from 'react-bootstrap/Form';

function Formcuenta() {
  const [formData, setFormData] = useState({
    email: '',
    telefono: '',
    contraseña: '',
    nombres: '',
    apellidos: '',
    cedula: '',
    usuario: '',
    confirmPassword: ''
  })

  const [formReady, setFormReady] = useState(false);

  // Función para verificar si todos los campos están completos
const checkFormCompletion = () => {
  // Verificar si todos los campos requeridos tienen un valor
  const isFormCompleted = Object.values(formData).every(value => value !== '');
  // Actualizar el estado de formReady
  setFormReady(isFormCompleted);
};

useEffect(() => {
  checkFormCompletion();
}, [formData]);

  const handleSubmit = (event) => {
    event.preventDefault();
     // Validar que las contraseñas coincidan
    if (formData.contraseña !== formData.confirmPassword) {
    // Mostrar mensaje de error o tomar otra acción según sea necesario
    console.log('Las contraseñas no coinciden');
    return;
  }

  if (formReady) {
      fetch('http://localhost:5000/insertar', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData),
      })
        .then(response => response.json())
        .then(data => {
          console.log('Respuesta del servidor:', data);
        })
        .catch(error => {
          console.error('Error al enviar datos:', error);
        });
    } else {
      // Mostrar un mensaje de error o tomar otra acción según sea necesario
      console.log('Por favor complete todos los campos antes de enviar el formulario.');
    }
  };
  

  const [showEmailForm, setShowEmailForm] = useState(false);
  const [showNombresForm, setShowNombresForm] = useState(false);
  const [showTelefonoForm, setShowTelefonoForm] = useState(false);
  const [showContraseñaForm, setShowContraseñaForm] = useState(false);

  const handleShowContraseñaForm = () => {
    setShowContraseñaForm(true);
    setShowTelefonoForm(false);
    setShowEmailForm(false);
    setShowNombresForm(false);
  }

  const handleShowTelefonoForm = () => {
    setShowTelefonoForm(true);
    setShowEmailForm(false);
    setShowNombresForm(false);
    setShowContraseñaForm(false);
  };

  const handleShowNombresForm = () => {
    setShowNombresForm(true);
    setShowEmailForm(false)
    setShowTelefonoForm(false);
    setShowContraseñaForm(false);
  };
  
  const handleShowEmailForm = () => {
    setShowEmailForm(true);
    setShowNombresForm(false);
    setShowTelefonoForm(false);
    setShowContraseñaForm(false);
  };


  return (
    <div className="contenedor-formcuenta">
      <div className="row justify-content-center">
        <div className="col-4">
          <div className="h4 text-center mb-4">Completa los datos para crear tu cuenta</div>
          
          {showEmailForm ? (
            // Mostrar el formulario para agregar el email
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="email" className="h5">Ingresa tu Email</label>
                <h5 className="h6">Asegúrate de tener acceso a él.</h5>
                <input 
                type="email" 
                className="form-control" 
                id="email" 
                value={formData.email} 
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                />
              </div>
              <Form.Group className="mb-3 form-check">
                <Form.Check type="checkbox" label="Acepto los Términos y condiciones y autorizo el uso de mis datos de acuerdo a la Declaración de Privacidad." />
              </Form.Group>
              <button type="submit" className="btn btn-warning botonn">Guardar</button>
            </form>
          ) : showTelefonoForm ? (
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="telefono" className="h5">Ingresa tu numero de teléfono</label>
                <h5 className="h6">Te enviaremos un código por SMS para validarlo. Con este teléfono podrás iniciar sesión en tu cuenta.</h5>
                <br></br>
                <div class="input-group">
                  <span class="input-group-text" id="basic-addon1">+593</span>
                  <input 
                  type="text" 
                  class="form-control" 
                  placeholder="Numero de telefono" 
                  value={formData.telefono} 
                  onChange={(e) => setFormData({ ...formData, telefono: e.target.value })} 
                  aria-label="Telefono" aria-describedby="basic-addon1" />
                </div>
              </div>
              <div class="form-check">
                <input class="form-check-input" type="checkbox" value="" id="invalidCheck" />
                <label class="form-check-label" for="invalidCheck">
                Acepto que me contacten por WhatsApp y/o SMS a este número
                </label>
                <div class="invalid-feedback">
                  You must agree before submitting.
                </div>
              </div>
              <button type="submit" className="btn btn-warning botonn">Guardar</button>
            </form>
          ) : showContraseñaForm ? (
            <Form onSubmit={handleSubmit}>
              <div className="form-group">
              <label htmlFor="password" className="h5">Crear contraseña</label>
                <input 
                type="password" 
                className="form-control" 
                id="password" 
                placeholder="Contraseña" 
                value={formData.password} 
                onChange={(e) => setFormData({ ...formData, password: e.target.value })} 
                />
              </div>
              <div class="form-check">
                <input class="form-check-input" type="checkbox" value="" id="invalidCheck" />
                <label class="form-check-label" for="invalidCheck">
                Mínimo 8 caracteres con letras y números.                
                </label>
              </div>
              <div class="form-check">
                <input class="form-check-input" type="checkbox" value="" id="invalidCheck" />
                <label class="form-check-label" for="invalidCheck">
                Sin secuencias como 1234 o ABCD.
                </label>
              </div>
              <div className="form-group">
                <label htmlFor="confirmPassword" className="h5">Confirmar contraseña</label>
                <input 
                type="password" 
                className="form-control" 
                id="confirmPassword" 
                placeholder="Confirmar Contraseña"
                value={formData.confirmPassword} 
                onChange={(e) => setFormData({ ...formData, confirmPassword: e.target.value })} 
                />
                {formData.password !== formData.confirmPassword && formData.confirmPassword !== '' && (
                  <div className="text-danger">Las contraseñas no coinciden.</div>
                )}
              </div>
              <button type="submit" className="btn btn-warning botonn">Guardar</button>
            </Form>
          ): showNombresForm ? (
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="nombres" className="h5">Elige cómo quieres que te llamemos</label>
                <h5 className="h6">Verán el nombre que elijas todas las personas que interactúen contigo y tu videoteca</h5>
              </div>
              <div className="row mb-3">
                <div className="col">
                  <label htmlFor="nombre" className="form-label">Nombre</label>
                  <input 
                  type="text" 
                  className="form-control" 
                  id="nombre" 
                  value={formData.nombres} 
                  onChange={(e) => setFormData({ ...formData, nombres: e.target.value })} 
                />
                  <div className="valid-feedback">Nombre invalido</div>
                </div>
                <div className="col">
                  <label htmlFor="apellido" className="form-label">Apellido</label>
                  <input 
                  type="text" 
                  className="form-control" 
                  id="apellido" 
                  value={formData.apellidos} 
                  onChange={(e) => setFormData({ ...formData, apellidos: e.target.value })} 
                />
                  <div className="valid-feedback">Apellido invalido</div>
                </div>
              </div>
              <div className="row mb-3">
                <div className="col">
                  <label htmlFor="cedula" className="form-label">Número de Cédula</label>
                  <input 
                  type="text" 
                  className="form-control" 
                  id="cedula" 
                  value={formData.cedula} 
                  onChange={(e) => setFormData({ ...formData, cedula: e.target.value })} 
                />
                </div>
                <div className="col">
                  <label htmlFor="validationCustomUsername" className="form-label">Nombre de usuario</label>
                  <div className="input-group has-validation">
                    <span className="input-group-text" id="inputGroupPrepend">@</span>
                    <input 
                    type="text" 
                    className="form-control" 
                    id="validationCustomUsername" 
                    value={formData.usuario} 
                    onChange={(e) => setFormData({ ...formData, usuario: e.target.value })} 
                    aria-describedby="inputGroupPrepend" 
                  />
                    <div className="invalid-feedback">Por favor elija otro nombre de usuario</div>
                  </div>
                </div>
              </div>
              <button type="submit" className="btn btn-warning botonn">Guardar</button>
            </form>
          ) : (
            // Mostrar los botones normales
            <ul className="list-unstyled">
              <li className="d-flex justify-content-between align-items-center mb-3">
                <label>Agrega tu e-mail</label>
                <button className="btn btn-warning botonn" onClick={handleShowEmailForm}>Agregar</button>
              </li>
              <li className="d-flex justify-content-between align-items-center mb-3">
                <label>Elige tu nombre</label>
                <button className="btn btn-warning botonn" onClick={handleShowNombresForm}>Agregar</button>
              </li>
              <li className="d-flex justify-content-between align-items-center mb-3">
                <label>Validar tu telefóno</label>
                <Link to="" className="btn btn-warning botonn" onClick={handleShowTelefonoForm}>Validar</Link>
              </li>
              <li className="d-flex justify-content-between align-items-center">
                <label>Crear contraseña</label>
                <Link to="" className="btn btn-warning botonn" onClick={handleShowContraseñaForm}>Crear</Link>
              </li>
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default Formcuenta;
