import React, { useState } from "react";
import { Link } from 'react-router-dom';

function Formcuenta() {
  const [email, setEmail] = useState("");
  const [nombre, setNombre] = useState("");
  const [telefono, setTelefono] = useState("");
  const [password, setPassword] = useState("");
  const [botonesHabilitados, setBotonesHabilitados] = useState([true, false, false, false]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes agregar la lógica para crear la cuenta, como validar los datos y llamar a una API
    console.log("Email:", email);
    console.log("Nombre:", nombre);
    console.log("Telefono:", telefono);
    console.log("Password:", password);
  };

  const validarDatos = () => {
    let botonesHabilitadosActualizados = [...botonesHabilitados];
    if (email !== "") {
      botonesHabilitadosActualizados[1] = true;
    } else {
      botonesHabilitadosActualizados[1] = false;
    }
    if (nombre !== "") {
      botonesHabilitadosActualizados[2] = true;
    } else {
      botonesHabilitadosActualizados[2] = false;
    }
    if (telefono !== "") {
      botonesHabilitadosActualizados[3] = true;
    } else {
      botonesHabilitadosActualizados[3] = false;
    }
    setBotonesHabilitados(botonesHabilitadosActualizados);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "email") {
      setEmail(value);
    } else if (name === "nombre") {
      setNombre(value);
    } else if (name === "telefono") {
      setTelefono(value);
    } else if (name === "password") {
      setPassword(value);
    }
    validarDatos();
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-6">
          <div className="h4">Completa los datos para crear tu cuenta</div>
          <ul>
            <li>
              <label>Agrega tu e-mail</label>
              <Link to="" className={botonesHabilitados[1] ? "btn btn-warning botonn" : "btn btn-secondary botonn"}>Agregar</Link>
            </li>
            <li>
              <label>Elige tu nombre</label>
              <Link to="" className={botonesHabilitados[2] ? "btn btn-warning botonn" : "btn btn-secondary botonn"}>Agregar</Link>
            </li>
            <li>
              <label>Validar tu telefóno</label>
              <Link to="" className={botonesHabilitados[3] ? "btn btn-warning botonn" : "btn btn-secondary botonn"}>validar</Link>
            </li>
            <li>
              <label>Crear contraseña</label>
              <Link to="" className={botonesHabilitados[4] ? "btn btn-warning botonn" : "btn btn-secondary botonn"}>Crear</Link>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Formcuenta;
