import React, { useState } from "react";
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import "../css/iniciar-sesion.css";

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!username || !password) {
      setError("Por favor, complete todos los campos.");
      return;
    }

    //FUNCION DE VALIDACION DE CREDENCIALES
    fetch('http://localhost:5000/login',{
      method:'POST',
      headers:{
        'Content-Type':'application/json'
      },
      body:JSON.stringify({username,password}),
    })
    .then(response=> response.json())
    .then(data =>{
      console.log(data);
    })
    .catch(error=>{
      console.error('Error al iniciar sesión:', error)
    });

    // Aquí puedes agregar la lógica para iniciar sesión, como validar las credenciales y llamar a una API
    console.log("Username:", username);
    console.log("Password:", password);
  };

  return (
    <div className="contenedor-login">
      <div className="login-form">
        <img src="../imagenes/imglogo.png"
          alt="Inicio"
          style={{ width: '100px', height: '100px' }}
          className="rounded mx-auto d-block"
        />
        <Form onSubmit={handleSubmit} className="d-flex flex-column align-items-center">
          {error && <></>}
          <Form.Group className="mb-3 w-50">
            <Form.Label>Nombre de usuario</Form.Label>
            <Form.Control type="username" placeholder="Ingrese nombre de usuario o correo electrónico" onChange={(e) => setUsername(e.target.value)} />
          </Form.Group>

          <Form.Group className="mb-3 w-50">
            <Form.Label>Contraseña</Form.Label>
            <Form.Control type="password" placeholder="Ingrese su contraseña" onChange={(e) => setPassword(e.target.value)} />
          </Form.Group>

          <Form.Group className="mb-3 form-check">
            <Form.Check type="checkbox" label="Remember me" />
          </Form.Group>

          <Button className="btn btn-warning botonn" variant="primary" type="submit">
            LOGIN
          </Button>

          <Form.Text className="text-muted mt-3">
            Forgot password ?
          </Form.Text>
        </Form>
      </div>
    </div>
  )
}

export default Login;
