import React, { useState } from "react";
import Slider from "react-slick"; // Importa el componente Slider de react-slick
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import logoImg from "../"; // Importa la imagen de manera adecuada

function Perfil() {
  const [showPerfil, setShowPerfil] = useState(true);

  // Configuración del slider
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className="contenedor-perfil">
      <div className="row text-center">
        <h1>Mi cuenta</h1>
      </div>
      <div className="row">
        {showPerfil ? (
          <div className="col-9">
            {/* Contenido del formulario */}
            <div className="form-floating mb-3">
              {/* Input 1 */}
            </div>
            <div className="form-floating mb-3">
              {/* Input 2 */}
            </div>
            {/* Resto del formulario */}
          </div>
        ) : (
          <div className="col-3">
            {/* Slider */}
            <Slider {...settings}>
              <div>
                <div className="row logo">
                  <img
                    src={logoImg} // Utiliza la imagen importada correctamente
                    alt="imagen-logo"
                    className="rounded float-start"
                    style={{ maxWidth: "125px" }}
                  />
                </div>
                <div className="row">
                  <ul className="list-unstyled">
                    <a href="/">
                      <li className="d-flex justify-content-between align-items-center mb-3">
                        <label>Mi perfil</label>
                      </li>
                    </a>
                    <a href="/">
                      <li className="d-flex justify-content-between align-items-center mb-3">
                        <label>Mis Compras</label>
                      </li>
                    </a>
                    <a href="/">
                      <li className="d-flex justify-content-between align-items-center mb-3">
                        <label>Mi negocio</label>
                      </li>
                    </a>
                    <a href="/">
                      <li className="d-flex justify-content-between align-items-center mb-3">
                        <label>Ventas</label>
                      </li>
                    </a>
                  </ul>
                </div>
              </div>
              {/* Agrega más slides aquí si es necesario */}
            </Slider>
          </div>
        )}
      </div>
    </div>
  );
}

export default Perfil;
