// Code: App.js
import './App.css';
import Navbar from './componentes/navbar.jsx';
import Screem from './componentes/screem.js';

function App() {
  const [videoSrc, setVideoSrc] = useState("https://www.youtube.com/embed/F3jePdO9_jc");
  const [gameTitle, setGameTitle] = useState("GOD OF WAR");
  const [gameDescription, setGameDescription] = useState("Esta nueva entrega para PlayStation 4, si bien mantendrá varios de los ingredientes indivisibles de su jugabilidad, apostará por un nuevo comienzo para el personaje y una ambientación nórdica.");
  const [imageSrc, setImageSrc] = useState("https://i.3djuegos.com/juegos/11552/god_of_war_ps4__nombre_temporal_/fotos/ficha/god_of_war_ps4__nombre_temporal_-3754795.jpg");

  const handleLinkClick = (video, title, description, image) => {
    setVideoSrc(video);
    setGameTitle(title);
    setGameDescription(description);
    setImageSrc(image);
};
  return (
    <div className="App">
      <Navbar />
      <Screem
                videoSrc={videoSrc}
                gameTitle={gameTitle}
                gameDescription={gameDescription}
                imageSrc={imageSrc}
                onLinkClick={handleLinkClick}
            />
    </div>
  );
}

export default App;
